//import SearchIcon from '@material-ui/icons/Search';

const styles = theme => ({
    root: {
        width: '100%',
    },
    grow: {
        flexGrow: 1,
    },
    menuButton: {
        marginLeft: -12,
        marginRight: 20,
    },
    title: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.white, 0.25),
        },
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing.unit,
            width: 'auto',
        },
    },
    searchIcon: {
        width: theme.spacing.unit * 9,
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
        width: '100%',
    },
    inputInput: {
        paddingTop: theme.spacing.unit,
        paddingRight: theme.spacing.unit,
        paddingBottom: theme.spacing.unit,
        paddingLeft: theme.spacing.unit * 10,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: 120,
            '&:focus': {
                width: 200,
            },
        },
    },
});

class Home extends React.Component {
    state = {
        expanded: null,
        data:[],
        loading:null,
    };


    async componentDidMount() {
        await this.getData();      
        }


    getData= async ()=>{
        this.setState({loading:true},()=> axios.get('https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=129f9eae3a934c2bb5cd1a8ae9807064')
        .then(response => {
        this.setState({
            loading:false,
            data:response.data.articles});
        }).catch((error)=>{
            console.log(error.message);          
        })
        )

    }

    // async getChecklistData() {
    //     this.setState({ loading: true }, () => instance.get(`checklist/get`).then((resp) => {
    //       this.setState({
    //         loading: false,
    //         list: resp.data.result.map((item) => {

    //         }),
    //       });
    //     }).catch((error) => {
    //       this.props.enqueueSnackbar({ "error": error });
    //     }));
    //   }




    showLoader=()=> {
        return (
           <div className="pre-loader" >
            Loading...
        </div>
        );
      }



    handleChange = id => (expanded) => {
        this.setState({
            expanded: expanded ? id : false,
        });
    };

    render() {
        const { classes } = this.props;
        const { expanded,data } = this.state;
        const  dataLength=data.length;
        return (
            <div className={classes.root}>
                <AppBar position="static">
                    <Toolbar>
                        <IconButton className={classes.menuButton} color="inherit" aria-label="Open drawer">
                            {/* <MenuIcon /> */}
                        </IconButton>
                        <Typography className={classes.title} variant="h6" color="inherit" noWrap>
                            Material-UI
                    </Typography>
                        <div className={classes.grow} />
                        <div className={classes.search}>
                            <div className={classes.searchIcon}>
                                {/* <SearchIcon /> */}
                            </div>
                            <InputBase
                                placeholder="Searchâ€¦"
                                classes={{
                                    root: classes.inputRoot,
                                    input: classes.inputInput,
                                }}
                            />
                        </div>
                    </Toolbar>
                </AppBar>
                <div>

                {this.state.loading===true
                ? this.showLoader()
                :
                <div>

                { this.state.data.map((value,id)=> (
                    <div>
                    <ExpansionPanel expanded={expanded === id} onChange={this.handleChange(id)}>
                        <ExpansionPanelSummary >
                                {/* <Typography className={classes.heading}>General settings</Typography>
                                <Typography className={classes.secondaryHeading}>I am an expansion ttttt</Typography> */}
                                <Grid container spacing={16}>
                                    <Grid item xs={2}> <img src={value.urlToImage} style={{width:'100px',height:'100px'}} alt="No Image found" /> </Grid>
                                    <Grid item xs={8}>{value.title}</Grid>
                                    <Grid item xs={2}>Pranshu</Grid>
                                </Grid>
                        </ExpansionPanelSummary>
                        <ExpansionPanelDetails>
                                <Typography>
                                     {value.content}
                                </Typography>
                        </ExpansionPanelDetails>
                    </ExpansionPanel>
            </div>


                ))
                }
            </div>
                }
                </div>

                </div>)

    }
}


