import React, { Component } from 'react'
import axios from "axios";

class Search extends Component {
    state = {
    query: '',
    }

    handleInputChange = () => {
    this.setState({
        query: this.search.value
    })
    }


    getInfo = () => {
            axios.get(`https://newsapi.org/v2/everything?q={QUERY}&from=2019-08-30&to=2019-08-30&sortBy=popularity&apiKey=129f9eae3a934c2bb5cd1a8ae9807064`)
            .then(({ data }) => {
                this.setState({
                results: data.data
                })
            })
        }

    render() {
    return (
        <div>
        <form>
        <input
            placeholder="Search for..."
            ref={input => this.search = input}
            onChange={this.handleInputChange}
        />
        <p>{this.state.query}</p>
        </form>

        </div>

    )
    }
    }

export default Search;
