import React, { Component } from 'react';
import axios from 'axios';
import Movies from './Presentation';
import Presentation from './Presentation';

class Search extends Component {
    state = {
        movies: null,
        loading: false,
        value: ''
    };

    search = async val => {
        this.setState({ loading: true });
        const res = await axios(
            `https://newsapi.org/v2/everything?q=${val}&from=2019-10-10&to=2019-10-10&sortBy=popularity&apiKey=129f9eae3a934c2bb5cd1a8ae9807064`
        );
        const movies = await res.data.results;
        this.setState({ movies, loading: false });
    };  

    onChangeHandler = async e => {
        this.search(e.target.value);
        this.setState({ value: e.target.value });
    };

    // get renderMovies() {
    //     console.log(this.state.movies)
    //     let movies = <h1>There's no articles</h1>;
    //     if (this.state.movies) {
    //     movies = <Presentation list={this.state.movies} />;
    //     }

    //     return movies;
    // }

    render() {
        return (
        <div>
            <input
            value={this.state.value}
            onChange={e => this.onChangeHandler(e)}
            placeholder="Type something to search"
            />
            <Presentation data={this.state.data} />
        </div>
        );
    }
}

export default Search;
