import React, { Component } from 'react';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import Grid from '@material-ui/core/Grid';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// import classnames from 'classnames';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import red from '@material-ui/core/colors/red';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import posed from 'react-pose';
import "./Home.scss";
import { Paper } from '@material-ui/core';


const Box = posed.div({
    hidden: { opacity: 0 },
    visible: { opacity: 1 }
});


const styles = theme => ({
    card: {
        maxWidth: '100%',
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
    actions: {
        display: 'flex',
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
});
class Presentation extends Component {
    state = {
        expanded: null,
        isVisible: true,
    }

    handleExpandClick = () => {
        this.setState(state => ({ expanded: !state.expanded }));
    };

    componentDidMount() {
        if (this.props.data.length === 0) {
            setInterval(() => {
                this.setState({ isVisible: !this.state.isVisible });
            }, 500);
        }
    }
    handleChange = id => (expanded) => {
        this.setState((preState) => {
            return { expanded: preState.expanded === id ? false : id }
        });
        // alert(id);
    };

    render() {
        const { expanded, isVisible } = this.state;
        const { classes } = this.props;
        return (
            <Grid container xs={12}>
                {
                    this.props.data.length === 0 ? <Grid container xs={12}>
                        <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
                    </Grid>
                        : <Grid container xs={12}>
                            <Grid item xs={6}>
                                <Card className={classes.card}>
                                    <CardHeader
                                        avatar={
                                            <Avatar aria-label="Recipe" className={classes.avatar}>
                                                M
            </Avatar>
                                        }
                                        action={
                                            <IconButton>
                                                <MoreVertIcon />
                                            </IconButton>
                                        }
                                        title={this.props.data[1].source.name}
                                        subheader={this.props.data[1].publishedAt}
                                    />
                                    <CardMedia
                                        className={classes.media}
                                        image={this.props.data[1].urlToImage}
                                        title="Paella dish"
                                    />
                                    <CardContent>
                                        <Typography component="p">
                                            This impressive paella is a perfect party dish and a fun meal to cook together with your
                                            guests. Add 1 cup of frozen peas along with the mussels, if you like.
          </Typography>
                                    </CardContent>
                                    <CardActions className={classes.actions} disableActionSpacing>
                                        <IconButton aria-label="Add to favorites">
                                            <FavoriteIcon />
                                        </IconButton>
                                        <IconButton aria-label="Share">
                                            <ShareIcon />
                                        </IconButton>
                                        {/* <IconButton
                                            className={classnames(classes.expand, {
                                                [classes.expandOpen]: this.state.expanded,
                                            })}
                                            onClick={this.handleExpandClick}
                                            aria-expanded={this.state.expanded}
                                            aria-label="Show more"
                                        >
                                            <ExpandMoreIcon />
                                        </IconButton> */}
                                    </CardActions>
                                    <Collapse in={this.state.expanded} timeout="auto" unmountOnExit>
                                        <CardContent>
                                            <Typography paragraph>Method:</Typography>
                                            <Typography paragraph>
                                                Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
                                                minutes.
            </Typography>
                                            <Typography paragraph>
                                                Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
                                                heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
                                                browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving
                                                chicken and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion,
                                                salt and pepper, and cook, stirring often until thickened and fragrant, about 10
                                                minutes. Add saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
            </Typography>
                                            <Typography paragraph>
                                                Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
                                                without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat
                                                to medium-low, add reserved shrimp and mussels, tucking them down into the rice, and
                                                cook again without stirring, until mussels have opened and rice is just tender, 5 to 7
                                                minutes more. (Discard any mussels that don’t open.)
            </Typography>
                                            <Typography>
                                                Set aside off of the heat to let rest for 10 minutes, and then serve.
            </Typography>
                                        </CardContent>
                                    </Collapse>
                                </Card>

                            </Grid>
                            <Grid item xs={6}>
                                {this.props.data.map((value, id) => (
                                    <div>
                                        <ExpansionPanel square expanded={expanded === id} onChange={this.handleChange(id)}>
                                            <ExpansionPanelSummary >
                                                <Grid container xs={12} spacing={16} wrap direction="row" justify="space-around" alignitems="flex-start">
                                                    <Grid item  >{value.title}</Grid>
                                                    {/* <Grid item > <img src={value.urlToImage} alt="no image found" style={{display: 'block',  maxWidth:'200px',  maxHeight:'95px', width: 'auto',  height: 'auto'}} /> </Grid> */}
                                                    {/* <Grid item >{value.source.name}</Grid>
                                                <Grid item >{value.publishedAt}</Grid> */}
                                                </Grid>
                                            </ExpansionPanelSummary>
                                            {/* <ExpansionPanelDetails>
                                            <Grid container xs={12} spacing={16} wrap direction="row" justify="space-around" alignitems="flex-start">
                                                <Grid item xs={6}> {value.content} </Grid>
                                                <Grid item ><a href={value.url} target="_blank" >{value.url} </a> </Grid>
                                                <Grid item xs={1} sm={1 }>{value.author} </Grid>
                                            </Grid>
                                        </ExpansionPanelDetails> */}
                                        </ExpansionPanel>
                                    </div>

                                ))
                                }
                            </Grid>
                        </Grid>


                }
            </Grid>
        )
    }
}


export default withStyles(styles)(Presentation);
