import React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import { fade } from '@material-ui/core/styles/colorManipulator';
import { withStyles } from '@material-ui/core/styles';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import Grid from '@material-ui/core/Grid';
import axios from "axios";

// import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
//import MenuIcon from '@material-ui/icons/Menu';
//import SearchIcon from '@material-ui/icons/Search';

const styles = theme => ({
    root: {
        width: '100%',
    },
    heading: {
        fontSize: theme.typography.pxToRem(15),
        flexBasis: '33.33%',
        flexShrink: 0,
      },
      secondaryHeading: {
        fontSize: theme.typography.pxToRem(15),
        color: theme.palette.text.secondary,
      },
    grow: {
        flexGrow: 1,
    },
    menuButton: {
        marginLeft: -12,
        marginRight: 20,
    },
    title: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.white, 0.25),
        },
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing.unit,
            width: 'auto',
        },
    },
    searchIcon: {
        width: theme.spacing.unit * 9,
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
        width: '100%',
    },
    inputInput: {
        paddingTop: theme.spacing.unit,
        paddingRight: theme.spacing.unit,
        paddingBottom: theme.spacing.unit,
        paddingLeft: theme.spacing.unit * 10,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: 120,
            '&:focus': {
                width: 200,
            },
        },
    },
});

class Home extends React.Component {
    state = {
        expanded: false,
        data:[],
        loading:null,
    };


    async componentDidMount() {
        await this.getData();      
        }


    getData= async ()=>{
        this.setState({loading:true},
            ()=> axios.get('https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=129f9eae3a934c2bb5cd1a8ae9807064')
        .then(response => {
        this.setState({
            loading:false,
            data:response.data.articles});
        }).catch((error)=>{
            console.log(error.message);          
        })
        )

    }

    showLoader=()=> {
        return (
            <div className="pre-loader" >
            Loading...
        </div>
        );
     }



    handleChange = id => (expanded) => {
        this.setState((preState)=>{
            return {expanded:preState.expanded===id?false:id  }
        });
    };

    render() {
        const { classes } = this.props;
        const { expanded,data } = this.state;
        const  dataLength=data.length;
        return (
            <div className={classes.root}>
                <AppBar position="static">
                    <Toolbar>
                        <IconButton className={classes.menuButton} color="inherit" aria-label="Open drawer">
                            {/* <MenuIcon /> */}
                        </IconButton>
                        <Typography className={classes.title} variant="h6" color="inherit" noWrap>
                            Material-UI
                    </Typography>
                        <div className={classes.grow} />
                        <div className={classes.search}>
                            <div className={classes.searchIcon}>
                                {/* <SearchIcon /> */}
                            </div>
                            <InputBase
                                placeholder="Search…"
                                classes={{
                                    root: classes.inputRoot,
                                    input: classes.inputInput,
                                }}
                            />
                        </div>
                    </Toolbar>
                </AppBar>
                <div>

                {this.state.loading===true
                ? this.showLoader()
                :
                <div>

                { this.state.data.map((value,id)=> (
                    <div>
                    <ExpansionPanel square expanded={expanded === id} onChange={this.handleChange(id)}>
                        <ExpansionPanelSummary >
                                {/* <Typography className={classes.heading}>General settings</Typography>
                                <Typography className={classes.secondaryHeading}>I am an expansion ttttt</Typography> */}
                                <Grid container spacing={16} >
                                    <Grid item xs={2}> <img src={value.urlToImage} alt="no image found" style={{width:'100px',height:'100px'}} /> </Grid>
                                    <Grid item xs={8}>{value.title}</Grid>
                                    <Grid item xs={1}>{value.source.name}</Grid>
                                    <Grid item xs={1}>{value.publishedAt}</Grid>
                                </Grid>
                        </ExpansionPanelSummary>
                        <ExpansionPanelDetails>
                        <Grid container spacing={16}>

                                    <Grid item xs={12}> {value.content} </Grid>
                                    <Grid item xs={10}><a href={value.url} target="_blank" >{value.url} </a> </Grid>
                                    <Grid item xs={2}>{value.author} </Grid>
                                </Grid>
                        </ExpansionPanelDetails>
                    </ExpansionPanel>
            </div>


                ))
                }
            </div>
                }
                </div>

                </div>)

    }
}


Home.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Home);
