import React, { Component } from 'react';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import Grid from '@material-ui/core/Grid';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// import classnames from 'classnames';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import red from '@material-ui/core/colors/red';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import ListView from "./List"
import posed from 'react-pose';
import "./Home.scss";
import { Paper } from '@material-ui/core';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader';
import Divider from '@material-ui/core/Divider';



const Box = posed.div({
    hidden: { opacity: 0 },
    visible: { opacity: 1 }
});


const styles = theme => ({
    root: {
        width: '100%',
        // maxWidth: 360,
        backgroundColor: theme.palette.background.paper,
        position: 'relative',
        overflow: 'auto',
        maxHeight: 600
    },
    listSection: {
        backgroundColor: 'inherit',
    },
    ul: {
        backgroundColor: 'inherit',
        padding: 0,
    },
    card: {
        maxWidth: '100%',
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
    actions: {
        display: 'flex',
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: red[500],
    },
});
class Presentation extends Component {
    state = {
        expanded: null,
        isVisible: true,
        id: 0,
    }

    handleExpandClick = () => {
        this.setState(state => ({ expanded: !state.expanded }));
    };

    componentDidMount() {
        if (this.props.data.length === 0) {
            setInterval(() => {
                this.setState({ isVisible: !this.state.isVisible });
            }, 500);
        }
    }
    handleChange = id => (expanded) => {
        this.setState((preState) => {
            return { expanded: preState.expanded === id ? false : id, id: id }
        });
        return id;
    };


    render() {
        const { expanded, isVisible, id } = this.state;
        const { classes, data } = this.props;
        return (
            <Grid container xs={12}>
                {
                    this.props.data.length === 0 ? <Grid container xs={12}>
                        <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
                    </Grid>
                        : <Grid container >
                            <Grid item xs={6} className="Left">
                                <Card className={classes.card}>
                                    <CardHeader
                                        avatar={
                                            <Avatar aria-label="Recipe" className={classes.avatar}>
                                                {data[id].source.name.charAt(0).toUpperCase()}
                                            </Avatar>
                                        }
                                        action={
                                            <IconButton >
                                                <ExpandMoreIcon />
                                            </IconButton>
                                        }
                                        title={data[id].source.name}
                                        subheader={data[id].publishedAt}
                                    />
                                    <CardMedia
                                        className={classes.media}
                                        image={data[id].urlToImage}
                                        title="Paella dish"
                                    />
                                    <CardContent>
                                        <Typography component="p">
                                            {data[id].content}
                                        </Typography>
                                    </CardContent>
                                    <CardActions className={classes.actions} disableActionSpacing>
                                        <IconButton aria-label="Add to favorites">
                                            <FavoriteIcon />
                                        </IconButton>
                                        <IconButton aria-label="Share">
                                            <ShareIcon />
                                        </IconButton>
                                        {/* <IconButton
                                            className={classnames(classes.expand, {
                                                [classes.expandOpen]: this.state.expanded,
                                            })}
                                            onClick={this.handleExpandClick}
                                            aria-expanded={this.state.expanded}
                                            aria-label="Show more"
                                        >
                                            <ExpandMoreIcon />
                                        </IconButton> */}
                                        <a href={this.props.data[1].url} target="_blank" >Go to</a>
                                    </CardActions>
                                    {/* <Collapse in={this.state.expanded} timeout="auto" unmountOnExit>
                                        <CardContent>
                                            <Typography paragraph>Method:</Typography>
                                            <Typography paragraph>
                                                Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
                                                minutes.
            </Typography>
                                            <Typography paragraph>
                                                Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
                                                heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
                                                browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving
                                                chicken and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion,
                                                salt and pepper, and cook, stirring often until thickened and fragrant, about 10
                                                minutes. Add saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
            </Typography>
                                            <Typography paragraph>
                                                Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
                                                without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat
                                                to medium-low, add reserved shrimp and mussels, tucking them down into the rice, and
                                                cook again without stirring, until mussels have opened and rice is just tender, 5 to 7
                                                minutes more. (Discard any mussels that don’t open.)
            </Typography>
                                            <Typography>
                                                Set aside off of the heat to let rest for 10 minutes, and then serve.
            </Typography>
                                        </CardContent>
                                    </Collapse> */}
                                </Card>

                            </Grid>
                            <Grid item xs={6} className="Right">
                                <List className={classes.root} subheader={<li />}>
                                    {data.map((value, id) => (
                                        <li key={`section-${value}`} className={classes.listSection}>
                                            <ul className={classes.ul} onClick={this.handleChange(id)}>
                                                <ListSubheader>{`${value.source.name}`}</ListSubheader>
                                                <ListItem button>
                                                    <ListItemText primary={value.title} />
                                                </ListItem>
                                                <Divider />
                                            </ul>
                                        </li>
                                    ))}
                                </List>
                            </Grid>
                        </Grid>


                }
            </Grid>
        )
    }
}


export default withStyles(styles)(Presentation);
