import React, { Component } from 'react';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import Grid from '@material-ui/core/Grid';
import posed from 'react-pose';
import "./Home.scss";
import { Paper } from '@material-ui/core';


const Box = posed.div({
    hidden: { opacity: 0 },
    visible: { opacity: 1 }
});

export default class Presentation extends Component {
    state = {
        expanded: null,
        isVisible: true,
    }

    componentDidMount() {
        if (this.props.data.length === 0) {
            setInterval(() => {
                this.setState({ isVisible: !this.state.isVisible });
            }, 500);
        }
    }
    handleChange = id => (expanded) => {
        this.setState((preState) => {
            return { expanded: preState.expanded === id ? false : id }
        });
    };

    render() {
        const { expanded, isVisible } = this.state;
        return (
            <Grid container xs={12}>
                {
                    this.props.data.length !== 0 ? <Grid container xs={12}>
                    <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
                   </Grid>
                        : <Grid container xs={12}>
                            <Grid item xs={6}>
                                <Paper >abc</Paper>
                                <Paper >abc</Paper>
                                <Paper >abc</Paper>
                                <Paper >abc</Paper><Paper >abc</Paper>
                                <Paper >abc</Paper><Paper >abc</Paper><Paper >abc</Paper><Paper >abc</Paper><Paper >abc</Paper>
                    </Grid>
                            <Grid item xs={6}>
                                <Paper>def</Paper>
                    </Grid>
                        </Grid>


                }
            </Grid>
        )
    }
}
